export const Book = (props) => {
  return (
    <article>
      <h2>{props.title}</h2>
      <h3>Author: {props.author}</h3>
      <p>Price: {props.price.toFixed(2)}</p>
      <footer>
        <span>Published by {props.edition}</span>
        in
        <span>{props.year}</span>
      </footer>
    </article>
  );
};
